package college.management.system;import net.proteanit.sql.DbUtils;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.sql.ResultSet;public class FreeStructure extends JFrame implements ActionListener {    FreeStructure() {        getContentPane().setBackground(Color.WHITE);        JLabel heading = new JLabel("Fee Structure");        heading.setBounds(400, 10, 400, 30);        heading.setFont(new Font("Tahoma", Font.BOLD, 30));        add(heading);        JTable table = new JTable();        try {            Conn c = new Conn();            ResultSet resultSet = c.statement.executeQuery("select * from fee");            table.setModel(DbUtils.resultSetToTableModel(resultSet));        } catch (Exception e) {            e.printStackTrace();        }        JScrollPane js = new JScrollPane(table);        js.setBounds(0, 60, 1000, 700);        add(js);        Dimension screenSize = Toolkit.getDefaultToolkit().getScreenSize();        int screenWidth = screenSize.width;        int screenHeight = screenSize.height;        setSize(screenWidth / 2, screenHeight / 2);        setLocation(100, 100);        setLayout(null);        setVisible(true);    }    public static void main(String[] args) {        new FreeStructure();    }    @Override    public void actionPerformed(ActionEvent e) {    }}