package college.management.system;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.sql.ResultSet;public class Login extends JFrame implements ActionListener {    static String user = "admin";    JTextField textFieldName;    JPasswordField passwordField;    JButton login, back;    Choice userC;    Login() {        JLabel labelName1 = new JLabel("LOGIN AS ADMIN/FACULTY/STUDENT");        labelName1.setBounds(80, 20, 250, 20);        add(labelName1);        JLabel labelName = new JLabel("Username");        labelName.setBounds(40, 50, 100, 20);        add(labelName);        textFieldName = new JTextField();        textFieldName.setBounds(150, 50, 150, 20);        add(textFieldName);        JLabel labelpass = new JLabel("Password");        labelpass.setBounds(40, 100, 100, 20);        add(labelpass);        passwordField = new JPasswordField();        passwordField.setBounds(150, 100, 150, 20);        add(passwordField);        JLabel loginaslabel = new JLabel("Login As");        loginaslabel.setBounds(40, 150, 100, 20);        add(loginaslabel);        userC = new Choice();        userC.setBounds(150, 150, 150, 20);        userC.add("ADMIN");        userC.add("FACULTY");        userC.add("STUDENT");        add(userC);        login = new JButton("Login");        login.setBounds(40, 200, 120, 30);        login.setBackground(Color.black);        login.setForeground(Color.white);        login.addActionListener(this);        add(login);        back = new JButton("Back");        back.setBounds(180, 200, 120, 30);        back.setBackground(Color.black);        back.setForeground(Color.white);        back.addActionListener(this);        add(back);        ImageIcon i1 = new ImageIcon(ClassLoader.getSystemResource("icon/second.png"));        Image i2 = i1.getImage().getScaledInstance(200, 200, Image.SCALE_DEFAULT);        ImageIcon i3 = new ImageIcon(i2);        JLabel img = new JLabel(i3);        img.setBounds(350, 20, 200, 200);        add(img);        JLabel help = new JLabel("<html>Please follow the below given instructions to log in:" + "<br>* Faculty members must use their <b>employee Id</b> in username field." + "<br>* Students must use their <b>Roll Numbers</b> in username field." + "<br>* In Password field, write your <b>date of births</b> in YYYY-MM-DD format." + "<br>* Both username and password should be correct in order to login successfully.");        help.setBackground(Color.yellow);        help.setForeground(Color.red);        help.setBounds(40, 270, 550, 80);        add(help);        ImageIcon i11 = new ImageIcon(ClassLoader.getSystemResource("icon/loginback.png"));        Image i22 = i11.getImage().getScaledInstance(600, 600, Image.SCALE_DEFAULT);        ImageIcon i33 = new ImageIcon(i22);        JLabel image = new JLabel(i33);        image.setBounds(0, 0, 600, 600);        add(image);        setSize(600, 600);        setLocation(100, 100);        setLayout(null);        setVisible(true);    }    public static void main(String[] args) {        new Login();    }    @Override    public void actionPerformed(ActionEvent e) {        if (e.getSource() == login) {            user = userC.getSelectedItem();            String username = textFieldName.getText();            String password = passwordField.getText();            String query = "select * from login where username='" + username + "' and password = '" + password + "'";            if (user.equalsIgnoreCase("admin"))                query = "select * from login where username='" + username + "' and password = '" + password + "'";            else if (user.equalsIgnoreCase("student")) {                query = "select * from student where rollno='" + username + "'";            } else if (user.equalsIgnoreCase("faculty")) {                query = "select * from teacher where empid='" + username + "'";            }            try {                Conn c = new Conn();                ResultSet resultSet = c.statement.executeQuery(query);                if (resultSet.next()) {                    if (user.equalsIgnoreCase("faculty") || user.equalsIgnoreCase("student"))                        if (!password.equalsIgnoreCase(resultSet.getString("dob"))) {                            JOptionPane.showMessageDialog(null, "Invalid username or password");                            int x = 2 / 0;                        }                    setVisible(false);                    new main_class();                } else {                    JOptionPane.showMessageDialog(null, "Invalid username or password");                }            } catch (Exception E) {                E.printStackTrace();            }            System.out.println(user);        } else {            setVisible(false);        }    }}