package college.management.system;import com.toedter.calendar.JDateChooser;import org.jdesktop.swingx.JXDatePicker;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.sql.ResultSet;import java.text.SimpleDateFormat;import java.util.Date;public class StudentLeave extends JFrame implements ActionListener {    Choice choiceRollNo, choTime;    JDateChooser selDate;    JButton submit, cancel;    Date selectedDate;    JXDatePicker datePicker;    StudentLeave() {        getContentPane().setBackground(new Color(210, 232, 252));        JLabel heading = new JLabel("Apply Leave (Student)");        heading.setBounds(40, 50, 300, 30);        heading.setFont(new Font("Tahoma", Font.BOLD, 20));        add(heading);        JLabel RollNoSE = new JLabel("Search by Roll Number");        RollNoSE.setBounds(60, 100, 200, 20);        RollNoSE.setFont(new Font("Tahoma", Font.PLAIN, 18));        add(RollNoSE);        choiceRollNo = new Choice();        choiceRollNo.setBounds(60, 130, 200, 20);        add(choiceRollNo);        try {            Conn c = new Conn();            ResultSet resultSet = c.statement.executeQuery("select * from student");            while (resultSet.next()) {                choiceRollNo.add(resultSet.getString("rollno"));            }        } catch (Exception e) {            e.printStackTrace();        }        JLabel lbldate = new JLabel("Date");        lbldate.setBounds(60, 180, 200, 20);        lbldate.setFont(new Font("Tahoma", Font.PLAIN, 18));        add(lbldate);        datePicker = new JXDatePicker();        datePicker.setBounds(60, 200, 150, 30);        add(datePicker);        JLabel time = new JLabel("Time Duration");        time.setBounds(60, 260, 200, 20);        time.setFont(new Font("Tahoma", Font.PLAIN, 18));        add(time);        choTime = new Choice();        choTime.setBounds(60, 290, 200, 20);        choTime.add("Full Day");        choTime.add("Half Day");        add(choTime);        submit = new JButton("Submit");        submit.setBounds(60, 350, 100, 25);        submit.setBackground(Color.black);        submit.setForeground(Color.white);        submit.addActionListener(this);        add(submit);        cancel = new JButton("Cancel");        cancel.setBounds(200, 350, 100, 25);        cancel.setBackground(Color.black);        cancel.setForeground(Color.white);        cancel.addActionListener(this);        add(cancel);        setSize(500, 550);        setLocation(550, 100);        setLayout(null);        setVisible(true);    }    public static void main(String[] args) {        new StudentLeave();    }    @Override    public void actionPerformed(ActionEvent e) {        if (e.getSource() == submit) {            String rollno = choiceRollNo.getSelectedItem();            selectedDate = datePicker.getDate();            SimpleDateFormat format1 = new SimpleDateFormat("dd-MM-yyyy");            String date = format1.format(selectedDate);            String time = choTime.getSelectedItem();            String Q = "insert into studentLeave(roll_no,time_duration,date) values('" + rollno + "','" + time + "',STR_TO_DATE('" + date + "','%d-%m-%Y'))";            try {                Conn c = new Conn();                c.statement.executeUpdate(Q);                JOptionPane.showMessageDialog(null, "Leave Confirmed");                setVisible(false);            } catch (Exception E) {                E.printStackTrace();            }        } else {            setVisible(false);        }    }}